import React, {useState} from 'react';

import './styles.css'

import Time from './Time'
import Partida from './Partida'


export default function PlacarContainer(props){

  const [golsCasa, setGolsCasa] = useState(0);
  const [golsVisitante, setGolsVisitante] = useState(0);


  // Dados

  const {casa,partida,visitante} = props


  function marcarGolCasa(){
    setGolsCasa(golsCasa+1)
  }

  function marcarGolVisitante(){
    setGolsVisitante(golsVisitante + 1)
  }

  return (
    <div className="container">
      <div className="time-casa">
        <h3>casa</h3>
        <Time nome={props.casa.nome} gols={golsCasa} marcarGol={marcarGolCasa}></Time>
      </div>

      <div className="partida">
        <Partida partida={props.partida}></Partida>
      </div>

      <div className="timeFora">
      <h3>visitante</h3>
        <Time nome={props.visitante.nome} gols={golsVisitante} marcarGol={marcarGolVisitante}></Time>
      </div>
    </div>
  )
}
